import { useState } from 'react'
import './index.css'
import 'react-bootstrap'
import { rangeValor } from '../range'
import { valorMaiusculas, valorMinusculas } from '../checkbox'
var i = 0





function Senha() {
    const [valor, SetValor] = useState('')

    function sortear() {
        let sorteio = 0
        let novaSenha = ''
        for (i = 0; i < rangeValor;) {
            sorteio = Math.floor(Math.random() * 4) + 1
            if (sorteio == 1 && valorMaiusculas==true) {
                let codigo = Math.floor(Math.random() * 26) + 65;
                let letra = String.fromCharCode(codigo);
                novaSenha = letra
                i++
            }
            if (sorteio==2 && valorMinusculas==true)
            {
                Math.floor(Math.random() * (122 - 97 + 1)) + 97;   
            }
        }
        SetValor(novaSenha)
    }

    return (
        <div className="senha">
            <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">Senha</span>
                <input type="text" className="form-control" aria-label="Username"
                    aria-describedby="basic-addon1" value={valor} disabled />
                <button type="button" onClick={sortear} className="btn btn-warning">Gerar</button>
            </div>
        </div>
    )
}



export default Senha
