import React, { useEffect, useState } from 'react'
import 'react-bootstrap'
import Senha, { j } from '../senha'
import Check, { valorExportado } from '../checkbox'
import './corpo.css'
import Range from '../range'
import { RegistroDAO } from '../../persistencia/registroDAO'
import { usuarioAtual } from '../../Login/Login'



function Corpo() {
    const [regs, SetRegs] = useState([]);
    const armazenado = new RegistroDAO();
    const armazenado2 = armazenado.registro.filter((element) => element.nome === usuarioAtual);
    const [j, setJ] = useState(0);

    useEffect(() => {
        SetRegs([...armazenado2]);
    }, []);

    useEffect(() => {
        const novosRegs = armazenado.registro.filter((element) => element.nome === usuarioAtual);
        if (novosRegs.length > regs.length) {
            SetRegs([...novosRegs]);
        }
    }, [j]);

    return (
        <section className='geral'>
            <div className='corpo'>
                <h3>Gerador de senhas. Configure para gerar a sua.</h3>
                <Senha />
                <Range />
                <div className='checks'>
                    <Check />
                </div>
                <table>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Senha</th>
                            <th>Utilidade</th>
                        </tr>
                    </thead>
                    <tbody>
                        {regs.map((element, index) => (
                            <tr key={index}>
                                <td>{element.usuario}</td>
                                <td>{element.senha}</td>
                                <td>{element.utilidade}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </section>
    );
}

export default Corpo;
