import { useState } from 'react'
import './index.css'
import 'react-bootstrap'
import { rangeValor } from '../range'
var i=0





function Senha() {
    const [valor,SetValor] = useState('')
    function sortear()
{
    for(i=0;i<rangeValor;i++)
    {
        let copia = valor
        console.log(copia)
        console.log(rangeValor)
        SetValor(copia+"a")
    }
}
    return(
        <div className="senha">
            <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">Senha</span>
                <input type="text" className="form-control" aria-label="Username"
                    aria-describedby="basic-addon1" value={valor} disabled/>
                    <button type="button" onClick={sortear} className="btn btn-warning">Gerar</button>
            </div>
        </div>
    )
}



export default Senha
